function init(){modeButtons(),squareListeners(),reset()}function modeButtons(){for(var e=0;e<mode.length;e++)mode[e].addEventListener("click",function(){mode[0].classList.remove("selected"),mode[1].classList.remove("selected"),this.classList.add("selected"),numSquares="Easy"===this.textContent?3:6,reset()})}function squareListeners(){for(var e=0;e<squares.length;e++)squares[e].addEventListener("click",function(){var e=this.style.backgroundColor;e===pickedColor?(messageDisplay.textContent="Correct",changeColors(e),h1.style.backgroundColor=e,resetButton.textContent="Play Again?"):(this.style.backgroundColor="#323232",messageDisplay.textContent="Try Again")})}function reset(){colors=generateRandomColors(numSquares),pickedColor=pickColor(),colorDisplay.textContent=pickedColor,h1.style.backgroundColor="steelblue",messageDisplay.textContent="",resetButton.textContent="New Colors";for(var e=0;e<squares.length;e++)colors[e]?(squares[e].style.display="block",squares[e].style.backgroundColor=colors[e]):squares[e].style.display="none"}function changeColors(e){for(var o=0;o<colors.length;o++)squares[o].style.backgroundColor=e}function pickColor(){var e=Math.floor(Math.random()*colors.length);return colors[e]}function generateRandomColors(e){for(var o=[],t=0;t<e;t++)o.push(randomColor());return o}function randomColor(){return"rgb("+Math.floor(256*Math.random())+", "+Math.floor(256*Math.random())+", "+Math.floor(256*Math.random())+")"}var colors=[],numSquares=6,pickedColor,squares=document.querySelectorAll(".square"),colorDisplay=document.getElementById("colorDisplay"),messageDisplay=document.querySelector("#message"),h1=document.querySelector("h1"),resetButton=document.querySelector("#reset"),mode=document.querySelectorAll(".mode");init(),resetButton.addEventListener("click",function(){reset()});