function changeColors(o){for(var r=0;r<colors.length;r++)squares[r].style.backgroundColor=o}function pickColor(){var o=Math.floor(Math.random()*colors.length);return colors[o]}function generateRandomColors(o){for(var r=[],e=0;e<o;e++)r.push(randomColor());return r}function randomColor(){return"rgb("+Math.floor(256*Math.random())+", "+Math.floor(256*Math.random())+", "+Math.floor(256*Math.random())+")"}var colors=generateRandomColors(6),squares=document.querySelectorAll(".square"),pickedColor=pickColor(),colorDisplay=document.getElementById("colorDisplay"),messageDisplay=document.querySelector("#message");colorDisplay.textContent=pickedColor;for(var i=0;i<squares.length;i++)squares[i].style.backgroundColor=colors[i],squares[i].addEventListener("click",function(){var o=this.style.backgroundColor;o===pickedColor?(messageDisplay.textContent="Correct",changeColors(o)):(this.style.backgroundColor="#232323",messageDisplay.textContent="Try Again")});